import React ,{Component} from 'react'
import RecData from './Data'

class Recommend extends Component {
    render(){
        return(
            RecData.map(
                (datas,i) =>{
                    return(
                        <div className = 'container-types container-recommend clearfix'>
                            <h3 className="types-title clearfix">         
                                <span>{datas.title[0]}</span>
                                <span className="intro">{datas.title[1]}</span>
                            </h3>
                            <div className = 'container-types container-recommend clearfix'>
                                <div className = 'path-list clearfix'>
                                    {
                                        i === '0'?
                                        datas.tag.map(
                                            (data) => 
                                                <a href="#" target="_blank">
                                                    <i className={data[0]}></i>
                                                    <p className="tit">{data[1]}</p>
                                                    <p className="desc">{data[2]}</p>
                                                </a>
                                        )
                                        :
                                        datas.img.map(
                                            (data,j) => 
                                                <a href="#" target="_blank" key = {j}>
                                                    <div className = 'recommend l'>
                                                        <img src = {data} alt = '' target="_blank"/>
                                                    </div>
                                                </a>
                                        )
                                    }
                                </div>
                            </div>
                        </div> 
                    )
                } 
            )
        )
    }
}

export default Recommend
