import React, {Component} from 'react';


class Box extends Component{
    render() {
        let tagBox = '';
        let title = '';
        const boxData = this.props.boxData;
        for(const data of (boxData)){
            tagBox = (data.tag).map((tag) => {
                return(
                    title = data.title,
                    <a href={data.href}>{tag}</a>
                )
            })
            console.log(tagBox)
        }  
        return(
            <div className="fe-base-box clearfix">
                <div className="banner-line">
                    <span className="bold mr10 l small-title">{title}</span>
                </div>
                <div className="tag-box l">
                    {tagBox}
                </div>
            </div>
        );
    }
}
export default Box
