import React ,{Component} from 'react'
import './GotoTop.css'


class GotoTop extends Component{
    onScrollHandle(){
        const scrollTop = this.scrollRef.scrollTop;
    }

    handleScrollTop(){
        window.scrollTo({
            left:0,
            top:0,
            behavior:'smooth',
        })
    }
    render(){
        return(
            <div id="J_GotoTop" class="elevator">    
                <a href="https://www.imooc.com/" class="elevator-msg" >        
                    <i class="icon-feedback"></i>        
                    <span>意见反馈</span>    
                </a>    
                <a href="https://www.imooc.com/" class="elevator-faq" >        
                    <i class="icon-ques"></i>        
                    <span>帮助中心</span>    
                </a>    
                <a href="https://www.imooc.com/"  class="elevator-app">        
                    <i class="icon-appdownload"></i>        
                    <span>APP下载</span>        
                    <div class="elevator-app-box"></div>    
                </a>    
                <a href="javascript:void(0)" class="elevator-weixin" id="js-elevator-weixin">        
                    <i class="icon-wxgzh"></i>        
                    <span>官方微信</span>        
                    <div class="elevator-weixin-box"></div>    
                </a>    
                <a href="javascript:void(0)" class="elevator-top no-goto" id="backTop"
                    ref={c => {this.scrollRef = c}}
                    style={{display:this.scrollTop>1500?'block':'none'}}
                    onClick = {this.handleScrollTop}
                    onScrollCapture={()=>this.onScrollHandle()}
                >        
                    <i class="icon-up2"></i>        
                    <span>返回顶部</span>   
                </a>
                <script>

                </script>
            </div>
        )
    }
}

export default GotoTop
