import React , {Component} from 'react'
import luxian from './Data'

class Bgfff2 extends Component{
    constructor(props){
        super(props);
        this.state=
            {
                curr : true,
            }
        }
    onClick= () =>{
        return(
            this.setState(
                {
                    curr : false,
                }
            )
        )
    }
    render(){
        return(
            <div className = 'bgfff'>
                <div className = 'container-types container-path clearfix'>
                    <h3 className = 'types-title clearfix'> 
                        <span>
                            {luxian.title}
                        </span>
                        <ul className="js-rank-tab">
                            {luxian.tag.map(
                                (data,i) => 
                                    <li id={i} className={this.state.curr?"curr":null} onClick = {this.onClick}>{data}</li> 
                            )}                                           
                        </ul>
                        <a href="https://www.imooc.com/" target="_blank" className="more">
                            查看更多
                             <i className="imv2-right2"></i>
                        </a>
                     </h3>
                     {
                        luxian.demo.map(
                            (data,j) => 
                                <div className = 'clearfix types-content js-path-content' key = {j}>
                                    {
                                        data.map(
                                            (card,i) => 
                                                <div className = 'course-path-container' key = {i}>
                                                    <a target="_blank" href="https://www.imooc.com/">                    
                                                        <div className="course-banner">                        
                                                            <div>
                                                                <img  className="img-up" src = {card.img} alt = ''/>
                                                            </div>                        
                                                            <div>
                                                                <img  className="img-mid" src = {card.img} alt = ''/>
                                                            </div>
                                                            <div>
                                                                <img  className="img-down" src = {card.img} alt = ''/>
                                                            </div>                    
                                                        </div>                    
                                                        <h4>{card.h4}</h4>                    
                                                        <p>{card.p}</p>
                                                        <div className="course-path-info">                        
                                                            <span>{card.span}</span>
                                                            <span><i className="imv2-star2"></i>{card.num}</span>
                                                        </div>                
                                                    </a>
                                                </div>
                                        )
                                    }
                                </div>
                            )
                    }
                </div>
            </div>
        )
    }
}

export default Bgfff2
