import React ,{Component} from 'react'
import './Body.css'
import MenuContent from './MenuContent'
import Submenu from './Submenu/Submenu'
import Lunbo from './Lunbo/Lunbo'

class Body extends Component {
    constructor(props){
        super(props)
        this.state = {
            itemId:'-1'
        }
    }
    getDataEnter = id => {
        return(
            this.setState({
                itemId:id
            })
        )
    }

    getDataLeave = id => {
        return(
            this.setState({
                itemId:id
            })
        )
    }

    onMouseLeave = () =>
        this.setState({
            itemId:'-1'
        })

    render(){
        return(
            <div id = 'main'>
                <div className='bk'> </div>
                <div className = 'bgfff banner-box'>
                    <div className = 'g-banner pr'>
                        <div className="menuwrap"></div>
                        <div onMouseLeave = {this.onMouseLeave}>
                            <MenuContent 
                                callBackEnter = {(data)=>this.getDataEnter(data)}
                            />
                            <Submenu 
                                setId = {this.state.itemId}
                            />
                            <Lunbo />
                        </div>

                    </div>
                    <div className = 'container-types container-recommend clearfix'>
                        <h3 class="types-title clearfix">         
                            <span>就业班</span>
                            <span class="intro">零基础快速就业</span>
                        </h3>
                        <div className = 'container-types container-recommend clearfix'>
                            <div className = 'path-list clearfix'>
                                <a href="#" target="_blank">
                                    <i class="i-web"></i>
                                    <p class="tit">Web前端攻城狮</p>
                                    <p class="desc">从未接触编程也可以学好</p>
                                </a>
                            </div>
                        </div>
                        <div className = 'container-types container-recommend clearfix'>
                            <a href="#" target="_blank">
                                <i class="i-web"></i>
                                <p class="tit">Web前端攻城狮</p>
                                <p class="desc">从未接触编程也可以学好</p>
                            </a>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Body
