import React ,{Component} from 'react'

class Card extends Component {
    constructor(props){
        super(props);
        this.state = {
            hide:true,
            num : -1
        }
    }
    onMouseEnter = () =>
        this.setState(
            {
                hide : false,
                num: this.index
            }
        )
    onMouseLeave = () => 
        this.setState(
            {hide : true}
        )
    render(){
        const card = this.props.card
        console.log(card)
        return(
            card.map((data,i) =>{
                return(
                    <div className = 'index-card-container course-card-container container' key = {data.index}>  
                        <a target="_blank" className="course-card" href = 'https://www.imooc.com/'>
                        {data.new?                        
                            <div className="course-stat new">
                                新课
                            </div>
                        :null
                        }
                            <div className="course-card-top hashadow">
                                <img className="course-banner" src={data.img}/>
                                <div className="course-label">
                                    {data.label.map(
                                        (label) =><label>{label}</label>
                                    )}
                                </div>
                            </div>
                            <div className="course-card-content">
                                    <h3 className="course-card-name">{data.cardname}</h3>
                                    <div className="clearfix course-card-bottom">
                                        <div className="course-card-info">
                                            <span>{data.cardinfo[0]}</span>
                                            <span><i className="imv2-set-sns"></i>{data.cardinfo[1]}</span>
                                            <span className="r js-hover-evaluation"
                                                onMouseEnter = {this.onMouseEnter}
                                                onMouseLeave = {this.onMouseLeave}
                                            >{data.cardinfo[2]}</span>
                                        </div>
                                        <div className = 'evaluation-desc-box clearfix' style = {{display:this.state.hide?'none':'block'}}>
                                            <i className="trangle"></i>
                                            <div className="left-box l">
                                                <p>{data.leftbox[0]}</p>
                                                <p className="big-text">{data.leftbox[1]}</p>
                                                <p>{data.leftbox[2]}</p>
                                            </div>
                                            <div className="rightcourse-card-container-box right-box l">
                                                <p>{data.rightbox[0]}<span>10.00</span></p>
                                                <p>{data.rightbox[1]}<span>10.00</span></p>
                                                <p>{data.rightbox[2]}<span>10.00</span></p>
                                            </div>
                                        </div>
                                        <div className="course-card-price">{data.price}</div>
                                    </div>
                                </div>
                        </a>
                    </div>
                )
            })
        )
    }
}

export default Card
