import React, {Component} from 'react'

const list = [
    {
        'title' : 'Vue',
        'class' : '',
        'href' : 'www.imooc.com',
        'target' : '_blank',
    },
    {
        'title' : 'Python',
        'class' : 'last',
        'href' : 'www.imooc.com',
        'target' : '_blank',
    }
]

const listitems = list.map((list) => {
    return(
        <a href={list.href} target={list.target} class={list.class}>{list.title}</a>
    )
    }
)

class Search extends Component{
    render(){ 
        return(
            <div className='search-warp clearfix'>
                <div class="search-area" data-search="top-banner">
                    <input class="search-input" type="text" data-suggest-trigger="suggest-trigger" placeholder="请输入关键字..." autocomplete="off"/>
                    <input class="btn_search" type="hidden" data-search-btn="search-btn" />
                    <div class="hotTags" style={{display:'block'}}>
                        {listitems}
                    </div>
                </div>
                <div class="showhide-search" data-show="no">
                    <i class="icon-search"></i>
                </div>
            </div>
        )
    }
}

export default Search
