import React, {Component} from 'react';
import subList from './Data'
import Box from './Box'
import RecommentBox from './RecommentBox'

class Submenu extends Component{
    constructor(props){
        super(props)
        this.state={
            show:false
        }
    }

    onMouseEnter = () =>{
        return (
            this.setState(
                {show:true}
            )
        )
    }

    onMouseLeave = () =>{
        return (
            this.setState(
                {show:false}
            )
        )
    }
    render() {
        const hide = this.props.hide
        console.log(hide)
        return(
            subList.map((subList,i) => {
                return(          
                    <div className={subList.subClassName}
                        onMouseLeave = {this.onMouseLeave}
                        onMouseEnter = {this.onMouseEnter}
                        style = {{display:this.state.show?'block':'none'}}
                    >
                        <div className="innerBox clearfix">
                            <div className="subinnerBox">
                                <div className="cates-box">
                                    <Box
                                        boxData = {subList.tagBoxList}
                                    />
                                </div>
                            </div>
                        </div>
                        <div className = 'recomment-box'>
                            <RecommentBox 
                                recData = {subList.recBoxList}
                            />    
                        </div>
                    </div>
                )
            })
        );
    }
}
export default Submenu
