import React, {Component} from 'react';
import LunboData from './Data';

class Lunbo extends Component{
    constructor(props) {
        super(props);
        this.state =  {
            imgIndex:0,
            imgState:true
        };
    }
    componentDidMount() {
        this.timerID = setInterval(
          () => this.timer(),
          2000
        );
    }

    componentWillUnmount() {
        this.stopTimer=clearInterval(this.timerID);
    }

    timer(){
        if(this.state.imgIndex){

            if (this.state.imgIndex === 5) {
                this.state.imgIndex = 0
            } else {
                this.state.imgIndex++
            }
        }else{
            return false
        }
    }

    // handleClickPrev = ()=>{

    //     return(
    //         this.setState(
    //             this.state.imgIndex--,
    //             this.state.imgIndex === -1?this.state.imgIndex= 5:this.state.imgIndex
    //         )
    //     )
    // }
    // handleClickNext = ()=>{
    //     return(
    //         this.setState(
    //             this.state.imgIndex++
    //         )
    //     )
    // }
    // tagClick(key){
    //     this.setState(
    //         this.state.imgIndex = key
    //     )
    // }
    render() {
        return(
            <div className="g-banner-content">
                <div className="g-banner-box" 
                    onMouseEnter= {this.timerID} 
                    onMouseLeave = {this.stopTimer}>
                    {LunboData.map((src,j) => 
                        <a href="https://www.imooc.com/" className="click-help " style = {{display:j === this.state.imgIndex?"block":"none"}}>
                            <div className="banner-slide">
                                <img src={src.img} alt = '' /> 
                            </div>
                        </a>
                    )}

                    <div className="banner-dots">
                        {
                            LunboData.map((tag,i) =>
                                <span className={i===this.state.imgIndex?"tab active":"tab"} key = {i}></span>
                            )
                        }
                    </div>
                    <a href="javascript:" className="banner-anchor prev icon-left2"></a>
                    <a href="javascript:" className="banner-anchor next icon-right2"></a>
                </div>
            </div>
        )
    }
}
export default Lunbo
