import React, {Component} from 'react'

const list = [
    {
        'target' : 'Java入门',
        'link' : 'https://www.imooc.com/'
    },
    {
        'target' : '前端入门',
        'link' : 'https://www.imooc.com/'
    }
]

const listitems = list.map((list) => {
    return(
        <a href={list.link}>{list.target}</a>
    )
    }
)

class Search extends Component{
    style = {
        'min-width': '32px',
        'height': '72px',
    }
    render(){ 
        return(
            <div className='search-warp clearfix' style = {style}>
                <div class="search-area" data-search="top-banner">
                    <input class="search-input" data-suggest-trigger="suggest-trigger" placeholder="请输入关键字..." type="text" autocomplete="off"/>
                    <input type="hidden" class="btn_search" data-search-btn="search-btn" />
                    <div class="hotTags" style="display: block;">
                        <a href="www.imooc.com" target="_blank" class="">Vue</a>
                        <a href="www.imooc.com" target="_blank" class="last">Python</a>
                    </div>
                    <div class="search-area-result nav-search-box js-search-wrap" style="">
                        <div class="hot">
                            <h2>热搜</h2>
                            <div class="hot-box"><a href="javascript:void(0)" class="hot-item js-history-search">Vue</a><a href="javascript:void(0)" class="hot-item js-history-search">Python</a><a href="javascript:void(0)" class="hot-item js-history-search">Java</a><a href="javascript:void(0)" class="hot-item js-history-search">mysql</a><a href="javascript:void(0)" class="hot-item js-history-search">flutter</a><a href="javascript:void(0)" class="hot-item js-history-search">React</a><a href="javascript:void(0)" class="hot-item js-history-search">GO语言</a><a href="javascript:void(0)" class="hot-item js-history-search">小程序</a></div>
                        </div>
                        <div class="history">
                            <ul data-suggest-result="suggest-result"></ul>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Search
