import React ,{Component} from 'react'

const menuData = ['前沿 / 区块链 / 人工智能','前端 / 小程序 / JS','后端 / JAVA / Python','移动 / Android / iOS','云计算 / 大数据 / 容器','运维 / 测试 / 数据库','UI设计 / 3D动画 / 游戏']

class MenuContent extends Component {
    onMouseEnter = (target) =>{
        // this.props.callBack&&this.props.callBack(target.target.id)
        console.log(target)
    }
    onMouseLeave = () =>{
        console.log("a")
    }
    render(){
        return(
            <div className = 'menuContent' >
                {
                    menuData.map((data,i)=>{
                        return(
                            console.log(i),
                            <div className="item" id = {i}
                                 onMouseEnter = {()=>onMouseEnter(1)}
                                 onMouseLeave = {this.onMouseLeave}
                            >
                                <a href="https://www.imooc.com/">
                                    <span className="group">{data}</span>
                                    <i className="imv2-arrow1_r"></i>
                                </a>
                            </div>
                        )
                    }) 
                }
            </div>
        )
    }
}

export default MenuContent
