import React ,{Component} from 'react'
import './Body.css'
import MenuContent from './MenuContent'
import Submenu from './Submenu/Submenu'

class Body extends Component {
    constructor(props){
        super(props)
        this.state = {
            itemId:'-1'
        }
    }
    getDataEnter = id => {
        return(
            this.setState({
                itemId:id
            }),
            onMouseEnter = id => 
                this.setState({
                    itemId:id
                })
        )
    }

    getDataLeave = id => {
        return(
            this.setState({
                itemId:id
            }),
            onMouseLeave = id => 
                this.setState({
                    itemId:id
                })
        )
    }

    render(){
        return(
            <div id = 'main'>
                <div className='bk'> </div>
                <div className = 'bgfff banner-box'>
                    <div className = 'g-banner pr'>
                        <div className="menuwrap"></div>
                        <div 
                            onMouseEnter = {this.getDataEnter.onMouseEnter}
                            onMouseLeave = {this.getDataLeave.onMouseLeave}
                        >
                            <MenuContent 
                                callBackEnter = {(data)=>this.getDataEnter(data)}
                                callBackLeave = {(data)=>this.getDataLeave(data)}
                            />
                            <Submenu 
                                setId = {this.state.itemId}
                            />
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Body
