import React ,{Component} from 'react'
import './GotoTop.css'

// let top = document.documentElement.scrollTop;
// let length = document.getElementById('backTop')

class GotoTop extends Component{
    constructor(props){
        super(props);
        this.state=(
            {
                toTop:0
            }
        )
    }
    scrollTop = window.scroll(
        ()=>{
            let top = document.documentElement.scrollTop;
            return(
                console.log(top),
                this.setState(
                    {toTop:top}
                )
            )
        }
    ) 
    render(){
        return(
            <div id="J_GotoTop" class="elevator">    
                <a href="https://www.imooc.com/" class="elevator-msg" >        
                    <i class="icon-feedback"></i>        
                    <span>意见反馈</span>    
                </a>    
                <a href="https://www.imooc.com/" class="elevator-faq" >        
                    <i class="icon-ques"></i>        
                    <span>帮助中心</span>    
                </a>    
                <a href="https://www.imooc.com/"  class="elevator-app">        
                    <i class="icon-appdownload"></i>        
                    <span>APP下载</span>        
                    <div class="elevator-app-box"></div>    
                </a>    
                <a href="javascript:void(0)" class="elevator-weixin" id="js-elevator-weixin">        
                    <i class="icon-wxgzh"></i>        
                    <span>官方微信</span>        
                    <div class="elevator-weixin-box"></div>    
                </a>    
                <a href="javascript:void(0)" class="elevator-top no-goto" id="backTop" style ={{display:this.state.top>1500?'block':'none'}}>        
                    <i class="icon-up2"></i>        
                    <span>返回顶部</span>   
                </a>
                <script>

                </script>
            </div>
        )
    }
}

export default GotoTop
